def get_dto_error_code_description(error_code):
    """Запрос описания ошибки ДТО"""
    if error_code in DTO_ERROR_DESCRIPTIONS:
        return DTO_ERROR_DESCRIPTIONS[error_code]
    else:
        return "НЕИЗВЕСТНАЯ ОШИБКА ДРАЙВЕРА! НЕОБХОДИМО АКТУАЛИЗИРОВАТЬ СЛОВАРЬ const/dto_error_descriptions.py"
